mapper.select:
select Count(*) from sys_permission

            and sys_permission.id=#{id} 
            and sys_permission.parent_id=#{parentId} 
            and sys_permission.menu_code=#{menuCode} 
            and sys_permission.menu_name=#{menuName} 
            and sys_permission.sort=#{sort} 
            and sys_permission.permission_code=#{permissionCode} 
            and sys_permission.permission_name=#{permissionName} 
            and sys_permission.required_permission=#{requiredPermission}

select * from sys_permission

            and sys_permission.id=#{id} 
            and sys_permission.parent_id=#{parentId} 
            and sys_permission.menu_code=#{menuCode} 
            and sys_permission.menu_name LIKE concat('%',concat(#{menuName},'%')) 
            and sys_permission.sort=#{sort} 
            and sys_permission.permission_code=#{permissionCode} 
            and sys_permission.permission_name LIKE concat('%',concat(#{permissionName},'%')) 
            and sys_permission.required_permission=#{requiredPermission} 

        order by sys_permission.${orderByCase}
        limit #{startPos},#{pageSize}
